import React, { useState } from "react";

function CreateDate({ handleAddDate }) {
  const [value, setValue] = useState({
    name: "",
    gender: "",
    see_again: false,
    date_of_date: "",
    image_uri: "",
  });

  function handleSubmit(event) {
    event.preventDefault();

    console.log("This is the event", event);

    fetch("/dates", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        comment: comment,
        score: score,
        user_id: userId,
        game_id: gameId,
      }),
    })
      .then((r) => r.json())
      .then((newReview) => onAddReview(newReview));

    console.log("date created: ", value);
    handleAddDate(value);
  }

  function handleChange(event) {
    setValue({ ...value, [event.target.name]: event.target.value });
  }

  return (
    <form class="ui form" onSubmit={handleSubmit}>
      <label>Name:Name:</label>
      {/* <input type="text" value={value} onChange={(e) => handleChange(e)} /> */}
      <input name="name" value={value.name} onChange={handleChange} />
      <br />
      <br />
      <label>specialty:Gender:</label>
      <input name="specialty" value={value.specialty} onChange={handleChange} />

      <br />
      <br />
      <label>weight:Date of Date:</label>
      <input name="weight" value={value.weight} onChange={handleChange} />
      <br />
      <br />
      <label>image:Image:</label>
      <input name="image" value={value.image} onChange={handleChange} />
      <br />
      <br />
      <label>greased:See again:</label>
      <input
        name="greased"
        type="radio"
        value={value.greased}
        onChange={handleChange}
      />
      <br />
      <br />
      <input type="submit" value="Submit" />
    </form>
  );
}
export default CreateDate;
