// import React, { useState, selectedDate } from "react";

function EditDate({ selectedDate }) {
  //   const [name, setName] = useState(props.name);
  //   const [age, setAge] = useState(props.age);

  const {
    date_of_date,
    gender,
    id,
    image_uri,
    locations,
    name,
    notes,
    see_again,
    user_id,
  } = selectedDate;
  console.log(date_of_date);
  console.log(gender);
  console.log(id);
  console.log(image_uri);
  console.log(locations);
  console.log(name);
  console.log(notes);
  console.log(see_again);
  console.log(user_id);

  function handleSubmit(event) {
    event.preventDefault();
  }

  return (
    <form onSubmit={handleSubmit}>
      <h2>Edit Form</h2>

      {/*  */}
      <label>Name:</label>
      {/* <input type="text" value={value} onChange={(e) => handleChange(e)} /> */}
      <input
        type="text"
        name="name"
        // value={value.name}
        // onChange={handleChange}
      />
      <br />
      <br />
      <label>Gender:</label>
      <input
        type="text"
        name="gender"
        // value={value.gender}
        // onChange={handleChange}
      />

      <br />
      <br />
      <label>Date of Date:</label>
      <input
        type="text"
        name="date_of_date"
        // value={value.date_of_date}
        // onChange={handleChange}
      />
      <br />
      <br />
      <label>Image:</label>
      <input
        type="text"
        name="image_uri"
        // value={value.image_uri}
        // onChange={handleChange}
      />
      <br />
      <br />
      <label>See again:</label>
      <input
        name="see_again"
        type="radio"
        // value={value.see_again}
        // onChange={handleChange}
      />
      <br />
      <br />
      {/*  */}
      <button type="submit">Save</button>
    </form>
  );
}

export default EditDate;
